(function ($) {
    "use strict";

    // Add 'toggled' class to the sidebar by default
    $(".sidebar").addClass("toggled");

    // Toggle the sidebar when the toggle buttons are clicked
    $("#sidebarToggle, #sidebarToggleTop").on("click", function (e) {
        $("body").toggleClass("sidebar-toggled");
        $(".sidebar").toggleClass("toggled");

        if ($(".sidebar").hasClass("toggled")) {
            $(".sidebar .collapse").collapse("hide");
        }
    });

    // Handle window resize events
    $(window).resize(function () {
        if ($(window).width() < 768) {
            $(".sidebar .collapse").collapse("hide");
        }
        // if ($(window).width() < 480 && !$(".sidebar").hasClass("toggled")) {
        //     $("body").addClass("sidebar-toggled");
        //     $(".sidebar").addClass("toggled");
        //     $(".sidebar .collapse").collapse("hide");
        // }
    });

    // Prevent the content wrapper from scrolling when the sidebar is hovered over on larger screens
    $("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function (e) {
        if ($(window).width() > 768) {
            var o = e.originalEvent.wheelDelta || -e.originalEvent.detail;
            this.scrollTop += 30 * (o < 0 ? 1 : -1);
            e.preventDefault();
        }
    });

    // Show or hide the scroll-to-top button based on scroll position
    $(document).on("scroll", function () {
        if ($(this).scrollTop() > 100) {
            $(".scroll-to-top").fadeIn();
        } else {
            $(".scroll-to-top").fadeOut();
        }
    });

    // Smooth scrolling to the top when the scroll-to-top button is clicked
    $(document).on("click", "a.scroll-to-top", function (e) {
        var o = $(this);
        $("html, body").stop().animate({
            scrollTop: $(o.attr("href")).offset().top
        }, 1000, "easeInOutExpo");
        e.preventDefault();
    });

})(jQuery);
